{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:resource:cache.redis:configuration:RedisCacheResourceConfiguration",
  "properties" : {
    "name" : {
      "title": "Cache name",
      "description": "The name of the cache resource.",
      "type" : "string",
      "default": "my-redis-cache"
    },
    "releaseCache": {
      "title": "Release the cache when API is stopped?",
      "description": "If enabled, the resource will release the cache. If not, you will have to manage it by yourself on your Redis server.",
      "type": "boolean",
      "default": false
    },
    "maxTotal" : {
      "title": "Max total",
      "description": "The maximum number of connections that are supported by the pool.",
      "type" : "integer",
      "default": "8"
    },
    "password": {
      "title": "Password",
      "description": "The password of the instance.",
      "type": "string",
      "writeOnly": true
    },
    "timeToLiveSeconds": {
      "title": "Time to live (in seconds)",
      "type" : "integer",
      "description": "The maximum number of seconds an element can exist in the cache regardless of use. The element expires at this limit and will no longer be returned from the cache. The default value is 0, which means no timeToLive (TTL) eviction takes place (infinite lifetime).",
      "default": 0,
      "minimum": 0
    },
    "timeout": {
      "title": "Timeout (in milliseconds)",
      "description": "The timeout parameter specifies the connection timeout and the read/write timeout.",
      "type": "integer",
      "default": 2000,
      "minimum": 0
    },
    "useSsl": {
      "title": "Use SSL",
      "description": "Use SSL connections.",
      "type": "boolean",
      "default": true
    },
    "standalone": {
      "type": "object",
      "title": "Standalone",
      "properties": {
        "enabled": {
          "type": "boolean",
          "title": "Use standalone mode",
          "default": true,
          "x-schema-form": {
            "disabled": [
              {
                "$eq": {
                  "sentinel.enabled": true
                }
              }
            ]
          }
        },
        "host" : {
          "title": "Host",
          "description": "The host of the instance",
          "type" : "string",
          "default": "localhost",
          "x-schema-form": {
            "disabled": [
              {
                "$eq": {
                  "sentinel.enabled": true
                }
              }
            ]
          }
        },
        "port" : {
          "title": "Port",
          "description": "The port of the instance",
          "type" : "integer",
          "default": 6379,
          "x-schema-form": {
            "disabled": [
              {
                "$eq": {
                  "sentinel.enabled": true
                }
              }
            ]
          }
        }
      },
      "oneOf": [
        {
          "properties": { "enabled": { "const": false } }
        },
        {
          "properties": { "enabled": { "const": true } },
          "required": ["host", "port"]
        }
      ]
    },
    "sentinel": {
      "type" : "object",
      "title": "Sentinel",
      "properties" : {
        "enabled": {
          "type": "boolean",
          "title": "Use sentinel mode",
          "description": "Sentinel provides high availability for Redis. In practical terms this means that using Sentinel you can create a Redis deployment that resists without human intervention certain kinds of failures.",
          "default": false
        },
        "masterId": {
          "title": "Master",
          "description": "The sentinel master id",
          "type": "string",
          "default": "sentinel-master",
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "sentinel.enabled": false
                }
              }
            ]
          }
        },
        "password": {
          "title": "Sentinel password",
          "description": "The sentinel password",
          "type": "string",
          "writeOnly": true,
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "sentinel.enabled": false
                }
              }
            ]
          }
        },
        "nodes" : {
          "type" : "array",
          "title": "Sentinel nodes",
          "items" : {
            "type" : "object",
            "title": "Node",
            "properties" : {
              "host" : {
                "title": "The host of node",
                "type" : "string",
                "default": "localhost"
              },
              "port" : {
                "title": "The port of node",
                "type" : "integer",
                "default": 26379
              }
            },
            "required": [
              "host",
              "port"
            ]
          },
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "sentinel.enabled": false
                }
              }
            ]
          }
        }
      },
      "oneOf": [
        {
          "properties": { "enabled": { "const": false } }
        },
        {
          "properties": { "enabled": { "const": true } },
          "required": ["masterId", "nodes"]
        }
      ]
    }
  },
  "required": [
    "name"
  ]
}

{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:resource:authprovider:ldap:LdapIdentityProviderConfiguration",
  "properties" : {
    "contextSourceUrl" : {
      "type" : "string",
      "title": "LDAP URL",
      "description": "The URL of the LDAP server to use. The URL should be in the format ldap://myserver.example.com:389. For SSL access, use the ldaps protocol and the appropriate port, e.g. ldaps://myserver.example.com:636. If fail-over functionality is desired, more than one URL can be specified, separated using comma (,).",
      "default": "ldap://myserver.example.com:389"
    },
    "useStartTLS": {
      "type": "boolean",
      "title": "Use StartTLS ?"
    },
    "contextSourceBase" : {
      "type" : "string",
      "title": "Base DN",
      "description": "The base DN. When this attribute has been configured, all Distinguished Names supplied to and received from LDAP operations will be relative to the specified LDAP path."
    },
    "contextSourceUsername" : {
      "type" : "string",
      "title": "Username",
      "description": "The username (principal) to use when authenticating with the LDAP server. This will usually be the distinguished name of an admin user (e.g.cn=Administrator), but may differ depending on server and authentication method."
    },
    "contextSourcePassword" : {
      "type" : "string",
      "title": "Password",
      "description": "The password (credentials) to use when authenticating with the LDAP server.",
      "widget": "password"
    },
    "userSearchFilter" : {
      "type" : "string",
      "title": "User search filter",
      "default": "uid={0}"
    },
    "userSearchBase" : {
      "type" : "string",
      "default": "ou=users",
      "title": "User search base",
      "description": "If user-search-base isn't supplied, the search will be performed from the root."
    },
    "attributes" : {
      "type" : "array",
      "title": "User LDAP attributes to put in the request context. Attributes can then be read from any other policy supporting EL (gravitee.attribute.user.{attribute})",
      "items" : {
        "type" : "string",
        "title": "Ldap attribute",
        "description": "Name of the LDAP attribute"
      }
    },
    "cacheMaxElements" : {
      "type" : "integer",
      "default": 100,
      "minimum": 0,
      "title": "Cache - Maximum number of elements",
      "description": "Maximum number of elements within the cache used to store successful authentications."
    },
    "cacheTimeToLive" : {
      "type" : "integer",
      "default": 60000,
      "minimum": 1000,
      "title": "Cache - Time To Live",
      "description": "Maximum time to live of the elements from the cache used to store successful authentications."
    },
    "connectTimeout" : {
      "type" : "integer",
      "default": 5000,
      "minimum": 0,
      "title": "Connect timeout",
      "description": "Duration of time in milliseconds that connects will block. (default 5000 ms)"
    },
    "responseTimeout" : {
      "type" : "integer",
      "default": 5000,
      "minimum": 0,
      "title": "Response timeout",
      "description": "Duration of time in milliseconds to wait for responses. (default 5000 ms)"
    },
    "minPoolSize" : {
      "type" : "integer",
      "default": 5,
      "minimum": 0,
      "title": "Min pool size",
      "description": "Minimum pool of connections to be initialized"
    },
    "maxPoolSize" : {
      "type" : "integer",
      "default": 15,
      "minimum": 0,
      "title": "Max pool size",
      "description": "Maximum pool of connections can grow to"
    }
  },
  "required": [
    "contextSourceUrl",
    "contextSourceBase",
    "contextSourceUsername",
    "contextSourcePassword",
    "userSearchFilter",
    "cacheMaxElements",
    "cacheTimeToLive"
  ]
}

{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:resource:authprovider:http:HttpIdentityProviderConfiguration",
