{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:resource:schema_registry:configuration:ConfluentSchemaRegistryConfiguration",
  "properties" : {
    "url" : {
      "title": "Registry URL",
      "description": "The schema registry url.",
      "type" : "string"
    },
    "proxy": {
      "type": "object",
      "title": "Proxy Options",
      "id": "urn:jsonschema:io:gravitee:resource:schema_registry:configuration:HttpProxyOptions",
      "properties": {
        "enabled": {
          "type": "boolean",
          "title": "Use proxy",
          "description": "Use proxy to fetch schema",
          "default": false
        },
        "type": {
          "type": "string",
          "title": "Proxy Type",
          "description": "The type of the proxy",
          "default": "HTTP",
          "enum": ["HTTP", "SOCKS4", "SOCKS5"],
          "x-schema-form": {
            "type": "select",
            "titleMap": {
              "HTTP": "HTTP CONNECT proxy",
              "SOCKS4": "SOCKS4/4a tcp proxy",
              "SOCKS5": "SOCKS5 tcp proxy"
            },
            "hidden": [
              {
                "$eq": {
                  "proxy.enabled": false
                }
              }
            ],
            "disabled": [
              {
                "$eq": {
                  "proxy.useSystemProxy": true
                }
              }
            ]
          }
        },
        "useSystemProxy": {
          "type": "boolean",
          "title": "Use system proxy",
          "description": "Use proxy configured at system level",
          "default": false,
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "proxy.enabled": false
                }
              }
            ]
          }
        },
        "host": {
          "type": "string",
          "title": "Proxy host",
          "description": "Proxy host to connect to",
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "proxy.enabled": false
                }
              }
            ],
            "disabled": [
              {
                "$eq": {
                  "proxy.useSystemProxy": true
                }
              }
            ]
          }
        },
        "port": {
          "type": "integer",
          "title": "Proxy port",
          "description": "Proxy port to connect to",
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "proxy.enabled": false
                }
              }
            ],
            "disabled": [
              {
                "$eq": {
                  "proxy.useSystemProxy": true
                }
              }
            ]
          }
        },
        "username": {
          "type": "string",
          "title": "Proxy username",
          "description": "Optional proxy username",
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "proxy.enabled": false
                }
              }
            ],
            "disabled": [
              {
                "$eq": {
                  "proxy.useSystemProxy": true
                }
              }
            ]
          }
        },
        "password": {
          "type": "string",
          "title": "Proxy password",
          "description": "Optional proxy password",
          "x-schema-form": {
            "type": "password",
            "hidden": [
              {
                "$eq": {
                  "proxy.enabled": false
                }
              }
            ],
            "disabled": [
              {
                "$eq": {
                  "proxy.useSystemProxy": true
                }
              }
            ]
          }
        }
      },
      "oneOf": [
        {
          "properties": { "enabled": { "const": false } }
        },
        {
          "properties": { "useSystemProxy": { "const": true } }
        },
        {
          "properties": { "enabled": { "const": true }, "useSystemProxy": { "const": false } },
          "required": ["host", "port"]
        }
      ]
    },
    "auth": {
      "type": "object",
      "title": "Authentication",
      "id": "urn:jsonschema:io:gravitee:resource:schema_registry:configuration:AuthOptions",
      "properties": {
        "mode": {
          "title": "Mode",
          "description": "The authentication mode to use to connect to Schema Registry",
          "type": "string",
          "default": "BASIC",
          "enum": ["NONE", "BASIC"],
          "x-schema-form": {
            "type": "select",
            "titleMap": {
              "NONE": "None",
              "BASIC": "Basic"
            }
          }
        },
        "username": {
          "type": "string",
          "title": "Username",
          "description": "The username",
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "auth.mode": "NONE"
                }
              }
            ]
          }
        },
        "password": {
          "type": "string",
          "title": "Password",
          "description": "The password",
          "x-schema-form": {
            "type": "password",
            "hidden": [
              {
                "$eq": {
                  "auth.mode": "NONE"
                }
              }
            ]
          }
        }
      },
      "oneOf": [
        {
          "properties": { "mode": { "const": "NONE" } }
        },
        {
          "properties": { "mode": { "const": "BASIC" } },
          "required": ["username", "password"]
        }
      ]
    },
    "ssl": {
      "type": "object",
      "title": "SSL Options",
      "id": "urn:jsonschema:io:gravitee:resource:schema_registry:configuration:SslOptions",
      "properties": {
        "hostnameVerifier": {
          "title": "Verify Host",
          "description": "Use to enable host name verification",
          "type": "boolean",
          "default": true
        },
        "trustAll": {
          "title": "Trust all",
          "description": "Use this with caution (if over Internet). The gateway must trust any origin certificates. The connection will still be encrypted but this mode is vulnerable to 'man in the middle' attacks.",
          "type": "boolean",
          "default": false
        },
        "trustStore": {
          "type": "object",
          "title": "Trust store",
          "id": "urn:jsonschema:io:gravitee:resource:schema_registry:configuration:SslTrustStoreOptions",
          "properties": {
            "type": {
              "type": "string",
              "title": "Type",
              "description": "The type of the trust store",
              "default": "",
              "enum": ["", "JKS", "PKCS12", "PEM"],
              "x-schema-form": {
                "type": "select",
                "titleMap": {
                  "": "None",
                  "JKS": "Java Trust Store (.jks)",
                  "PKCS12": "PKCS#12 (.p12) / PFX (.pfx)",
                  "PEM": "PEM (.pem)"
                }
              }
            },
            "password": {
              "type": "string",
              "title": "Password",
              "description": "Trust store password",
              "x-schema-form": {
                "type": "password",
                "hidden": [
                  {
                    "$eq": {
                      "ssl.trustStore.type": ["", "PEM"]
                    }
                  }
                ]
              }
            },
            "path": {
              "type": "string",
              "title": "Path to trust store",
              "description": "Path to the trust store file",
              "x-schema-form": {
                "hidden": [
                  {
                    "$eq": {
                      "ssl.trustStore.type": ""
                    }
                  }
                ]
              }
            },
            "content": {
              "type": "string",
              "title": "Content",
              "description": "Binary content as Base64",
              "x-schema-form": {
                "type": "text",
                "hidden": [
                  {
                    "$eq": {
                      "ssl.trustStore.type": ""
                    }
                  }
                ]
              }
            }
          },
          "oneOf": [
            {
              "properties": { "type": { "const": "" } }
            },
            {
              "properties": { "type": { "const": "PEM" } },
              "required": ["content"]
            },
            {
              "properties": { "type": { "const": "PEM" } },
              "required": ["path"]
            },
            {
              "properties": { "type": { "pattern": "JKS|PKCS12" } },
              "required": ["content", "password"]
            },
            {
              "properties": { "type": { "pattern": "JKS|PKCS12" } },
              "required": ["path", "password"]
            }
          ],
          "x-schema-form": {
            "hidden": [
              {
                "$eq": {
                  "ssl.trustAll": true
                }
              }
            ]
          }
        },
        "keyStore": {
          "type": "object",
          "title": "Key store",
          "id": "urn:jsonschema:io:gravitee:resource:schema_registry:configuration:SslKeyStoreOptions",
          "properties": {
            "type": {
              "type": "string",
              "title": "Type",
              "description": "The type of the key store",
              "default": "",
              "enum": ["", "JKS", "PKCS12", "PEM"],
              "x-schema-form": {
                "type": "select",
                "titleMap": {
                  "": "None",
                  "JKS": "Java Trust Store (.jks)",
                  "PKCS12": "PKCS#12 (.p12) / PFX (.pfx)",
                  "PEM": "PEM (.pem)"
                }
              }
            },
            "password": {
              "type": "string",
              "title": "Password",
              "description": "Password to use to open the key store",
              "x-schema-form": {
                "type": "password",
                "hidden": [
                  {
                    "$eq": {
                      "ssl.keyStore.type": ["", "PEM"]
                    }
                  }
                ]
              }
            },
            "alias": {
              "type": "string",
              "title": "Alias for the key",
              "description": "Alias of the key to use in case the key store contains more than one key",
              "x-schema-form": {
                "type": "password",
                "hidden": [
                  {
                    "$eq": {
                      "ssl.keyStore.type": ["", "PEM"]
                    }
                  }
                ]
              }
            },
            "keyPassword": {
              "type": "string",
              "title": "Key Password",
              "description": "Password to use to access the key when protected by password",
              "x-schema-form": {
                "type": "password",
                "hidden": [
                  {
                    "$eq": {
                      "ssl.keyStore.type": ["", "PEM"]
                    }
                  }
                ]
              }
            },
            "path": {
              "type": "string",
              "title": "Path to key store",
              "description": "Path to the key store file",
              "x-schema-form": {
                "hidden": [
                  {
                    "$eq": {
                      "ssl.keyStore.type": ["", "PEM"]
                    }
                  }
                ]
              }
            },
            "content": {
              "type": "string",
              "title": "Content",
              "description": "Binary content as Base64",
              "x-schema-form": {
                "type": "text",
                "hidden": [
                  {
                    "$eq": {
                      "ssl.keyStore.type": ["", "PEM"]
                    }
                  }
                ]
              }
            },
            "certPath": {
              "type": "string",
              "title": "Path to cert file",
              "description": "Path to cert file (.PEM)",
              "x-schema-form": {
                "hidden": [
                  {
                    "$neq": {
                      "ssl.keyStore.type": "PEM"
                    }
                  }
                ]
              }
            },
            "certContent": {
              "type": "string",
              "title": "Certificate",
              "x-schema-form": {
                "type": "text",
                "hidden": [
                  {
                    "$neq": {
                      "ssl.keyStore.type": "PEM"
                    }
                  }
                ]
              }
            },
            "keyPath": {
              "type": "string",
              "title": "Path to private key file",
              "description": "Path to private key file (.PEM)",
              "x-schema-form": {
                "hidden": [
                  {
                    "$neq": {
                      "ssl.keyStore.type": "PEM"
